                     <script class="id-6Z-b4QPEz-932">
                        document.addEventListener('DOMContentLoaded', function () {
                            class CRMForm {
                                constructor(formElement) {
                                    this.form = formElement;
                        
                                    this.formId = formElement.dataset.formId;
                                    this.otpContainer = formElement.querySelector('.otp-verification-container');
                                    this.submitButton = formElement.querySelector('.crm-form-submit-button');
                                    this.mobileInput = formElement.querySelector('.crm-form-input-mobile');
                                    this.cityInput = formElement.querySelector('.crm-form-input-city');
                                    this.investmentInput = formElement.querySelector('.crm-form-input-state');
                        
                                    this.hideOtpVerificationContainer();
                                    this.bindEvents();
                                }
                        
                                bindEvents() {
                                    this.form.addEventListener('submit', (e) => this.handleSubmit(e));
                                    this.mobileInput.addEventListener('input', () => this.resetOtpVerification());
                                }
                        
                                hideOtpVerificationContainer() {
                                    this.otpContainer.style.display = 'none';
                                }
                        
                                resetOtpVerification() {
                                    this.form.querySelector('.crm-form-input-otp').value = '';
                                    this.hideOtpVerificationContainer();
                                }
                        
                                disableSubmitButton(disable) {
                                    this.submitButton.disabled = disable;
                                }
                        
                                async handleSubmit(e) {
                                    e.preventDefault();
                                    this.disableSubmitButton(true);
                        
                                    const formData = new FormData(this.form);
                                    const formDataFields = Object.fromEntries(formData.entries());
                        
                                    const mobile = formData.get('mobile');
                                    const name = formData.get('name');
                                    const email = formData.get('email');
                                    const city = formData.get('city');
                                    const investment_capacity = formData.get('investment_capacity');
                                    const otp = formData.get('otp');
                        
                                    try {
                                        if (!/^\d{10}$/.test(mobile)) {
                                            throw new Error('Please enter a valid 10 digit mobile number');
                                        }
                        
                                        if (!otp) {
                                            await this.generateOTP(formDataFields);
                                            return false;
                                        }
                        
                                        const response = await fetch('https://jetking.alignwebs.com/api/submit', {
                                            method: 'POST',
                                            headers: {
                                                'Content-Type': 'application/json',
                                                Accept: 'application/json',
                                            },
                                            body: JSON.stringify({
                                                mobile,
                                                name,
                                                email,
                                                city,
                                                investment_capacity,
                                                otp
                                            }),
                                        });
                        
                                        if (!response.ok) {
                                            const errorData = await response.json();
                                            throw new Error(errorData.message || 'Unknown error');
                                        }
                        
                                        const data = await response.json();
                                        window.location.href = 'http://www.jetking.com/thank-you';
                        
                                    } catch (error) {
                                        alert(error.message);
                                    } finally {
                                        this.disableSubmitButton(false);
                                    }
                                }
                        
                                async generateOTP(formDataFields) {
                                    try {
                                        const response = await fetch('https://jetking.alignwebs.com/api/generateOTP', {
                                            method: 'POST',
                                            headers: {
                                                'Content-Type': 'application/json',
                                                Accept: 'application/json',
                                            },
                                            body: JSON.stringify(formDataFields),
                                        });
                        
                                        if (!response.ok) {
                                            const errorData = await response.json();
                                            throw new Error(errorData.message || 'Unknown error');
                                        }
                        
                                        const data = await response.json();
                                        const otpMessage = this.otpContainer.querySelector('p');
                                        otpMessage.textContent = `We have sent an OTP to your mobile number ${formDataFields.mobile}. Please enter the OTP to verify your mobile number.`;
                                        this.otpContainer.style.display = 'block';
                        
                                    } catch (error) {
                                        alert(error.message);
                                    } finally {
                                        this.disableSubmitButton(false);
                                    }
                                }
                            }
                        
                            // Initialize all forms on the page
                            document.querySelectorAll('.crm-form').forEach(form => new CRMForm(form));
                        });
                     </script>
